// Beispiel: BeastDetail.js
import React from "react";
import { useParams } from "react-router-dom";
import { BeastData } from "../../BeastData";

function BeastDetail() {
  const { id } = useParams(); // Holen Sie sich die ID aus den Routenparametern

  // Suchen Sie die Kreatur mit der entsprechenden ID
  const beast = BeastData.find((beast) => beast.id === parseInt(id));

  if (!beast) {
    return <div>Kreatur nicht gefunden</div>;
  }

  return (
    <div>
      <h2>{beast.name}</h2>
      <img src={beast.image} alt={beast.name} />
      <p>{beast.description}</p>
      <p>Preis: {beast.price}</p>
    </div>
  );
}

export default BeastDetail;
